import React, { Component } from "react";
import Space from './Space/Space';
import './App.css';
import Home from './home/home';
import Particle from './Particles/Particles';
import Navigation from './Navigation/navigation';
import Logo from './logo/logo';
import Signin from './Signin/Signin';
import Register from './Register/Register';
// import Bars from './Bars/Bars';
import Search from './search/Search';
import LocationSearchModal from './Map/Map';
import Creecov from './creecov/creecov';
import Demandercov from './demandercov/demandercov';
import Questions from './Questions/Questions'
import Join from './Join/Join';
import Chat from './Chat/Chat';
import About from './About/About';
import Profil from './profile/profile';
import { BrowserRouter as Router, Route } from "react-router-dom";

class App extends Component {

constructor(){
  super();
  this.state={
    input:'',
    route:'homep',
    isSignedIn:false,
    session:'normal'
    
  }
}

onRouteChange=(route) =>{
 if(route==="signin" || route==="registrer"){
    this.setState({isSignedIn:false})
  } else {
    this.setState({isSignedIn:true})
  }
  this.setState({route:route})
}
onSessionChange=(session) =>{
  this.setState({session:session})
}

  render(){
  return (
    <div>
   { this.state.route==='homep'  && ( this.state.session==='normal' || this.state.session==='admin')
    ? 
    <div>
    <Space/>
    <Home onRouteChange={this.onRouteChange}/>
    </div>
    :
    <div>
    <Particle/>
     <Navigation isSignedIn={this.state.isSignedIn} onRouteChange={this.onRouteChange}/>
    <Logo/>
    { this.state.route==='signin' && ( this.state.session==='normal' || this.state.session==='admin')
      ? <Signin onRouteChange={this.onRouteChange} onSessionChange={this.onSessionChange} />
      : ((this.state.route==='home' || this.state.route==='payee' || this.state.route==='mine' || this.state.route==='save') && ( this.state.session==='normal' ) 
         ?<Search route={this.state.route} session={this.state.session} />
         :(this.state.route==='map' && ( this.state.session==='normal' || this.state.session==='admin')
         ?<LocationSearchModal/>
         :(this.state.route==='registrer' 
         ?<Register onRouteChange={this.onRouteChange}/>
         :(this.state.route==='creecov' && ( this.state.session==='normal' || this.state.session==='admin')
          ?<Creecov onRouteChange={this.onRouteChange} />
         :(this.state.route==='demandercov'  && ( this.state.session==='normal' || this.state.session==='admin')
          ?<Demandercov/> 
         :(this.state.route==='chat'  && ( this.state.session==='normal' || this.state.session==='admin')
          ?<Router><Route path="/" exact component={Join} /><Route path="/chat" component={Chat} /></Router>
         :(this.state.route==='ques'  && ( this.state.session==='normal' || this.state.session==='admin')
          ?<Questions/>
         :(this.state.route==='about' && ( this.state.session==='normal' || this.state.session==='admin')
            ?<About/>
         :(this.state.route==='profile' && ( this.state.session==='normal')
          ?<Profil/>
          :(this.state.session==='admin' && this.state.route==='home'
            ?<Search route={this.state.route} session={this.state.session}/>
            :<div></div>)))))))))
)
    }


    </div>

   
  }
  {/*<LocationSearchModal/>*/}
   </div>
  
  );
}
}

export default App;
