import React,{Component} from 'react';
import Cardlist from './Cardlist';
import Searchbox from './Searchbox';
import {users} from './robots';
 import moment from 'moment';
import {Info} from './pay';

class Search extends Component{

  updateItem(id, itemAttributes) {
  var index = this.state.robots.findIndex(x=> x.id === id);

    this.setState({
      items: [
         ...this.state.robots.slice(0,index),
         Object.assign({}, this.state.robots[index], itemAttributes),
         ...this.state.robots.slice(index+1)
      ]
    });
}

  constructor(){
    super()
    this.state={
    	robots: [],
    	searchfield1:'',
      searchfield2:'',
      searchfield3:'',
      payInfo:Info,
      rca:'housam'
    }

  }

  componentDidMount(){
  this.setState({robots:users})
  	
  }
  
onSearchchange1 = (event) => {
  
  this.updateItem(1, {name:'rca'});

   console.log(this.state.robots[0].name);
   console.log(this.state.robots[1].name);
   console.log(this.state.robots[2].name);
}
onSearchchange2 = (event) => {
  this.setState({searchfield2: event.target.value });

}
onSearchchange3 = (event) => {
  this.setState({searchfield3: event.target.value });
   
}


onPayment=(x)=>{
    
  this.state.payInfo.map((user)=>{
       this.setState({rca:'true' });
        console.log(this.state.rca) ;
        
    })
  console.log(this.state.payInfo[0].payment);
  console.log(x.id);
}

	render(){


		const filterrobots=this.state.robots.filter(robots =>{
		return (robots.depart.toLowerCase().includes(this.state.searchfield1.toLowerCase())
	         & robots.arrive.toLowerCase().includes(this.state.searchfield2.toLowerCase())
           & moment(robots.date).format("YYYY-DD-MM").includes(this.state.searchfield3)
            )
  })
  if (this.state.robots.length === 0) {
    return <h1> LOADING ... </h1>
  } else {
  return(
  	 <div className='tc'>
  	   
      <Searchbox searchchange1={this.onSearchchange1} searchchange2={this.onSearchchange2} searchchange3={this.onSearchchange3}/>
      <Cardlist robots={filterrobots} onPayment={this.onPayment}/>
     
     </div>
  	);
}
  }
}
export default Search;
